import { parseArrowSync, parseArrowInBatches } from "./lib/parsers/parse-arrow.js";
import { ArrowWorkerLoader } from "./exports/arrow-loader.js";
/** ArrowJS table loader */
export const ArrowLoader = {
    ...ArrowWorkerLoader,
    parse: async (arraybuffer, options) => parseArrowSync(arraybuffer, options?.arrow),
    parseSync: (arraybuffer, options) => parseArrowSync(arraybuffer, options?.arrow),
    parseInBatches: parseArrowInBatches
};
